for path in `echo "select c_path from sogo_cache_folder_$1" | mysql --defaults-file=/etc/mysql/debian.cnf sogo`; do rm /tmp/foo.b64; echo "Reading $path"; if [[ $path != "c_path" ]]; then echo "select c_content from sogo_cache_folder_u52 where c_path = '$path' INTO OUTFILE '/tmp/foo.b64' FIELDS ESCAPED BY ''" | mysql --defaults-file=/etc/mysql/debian.cnf sogo; base64 -d /tmp/foo.b64 > /tmp/foo.bson; obj/sogo-tests; read; fi; done
